// src/firebase.js

// Import the required Firebase modules (modular SDK)
import { initializeApp } from "firebase/app";
import { getAuth } from "firebase/auth";
import { getFirestore } from "firebase/firestore";

// Firebase configuration object
// Replace these placeholders with your actual Firebase project credentials
const firebaseConfig = {
  apiKey: processYOUR_API_KEY,  // Add your Firebase API Key here
  authDomain: "YOUR_AUTH_DOMAIN",  // Add your Firebase Auth Domain here
  projectId: "YOUR_PROJECT_ID",  // Add your Firebase Project ID here
  storageBucket: "YOUR_STORAGE_BUCKET",  // Add your Firebase Storage Bucket here
  messagingSenderId: "YOUR_MESSAGING_SENDER_ID",  // Add your Firebase Sender ID here
  appId: "YOUR_APP_ID",  // Add your Firebase App ID here
  measurementId: "YOUR_MEASUREMENT_ID"  // Optional (if available)
};

// Initialize Firebase with the config object
const app = initializeApp(firebaseConfig);

// Initialize Firebase Authentication and Firestore
export const auth = getAuth(app);  // Firebase Authentication
export const db = getFirestore(app);  // Firebase Firestore Database

export default app;  // Export the initialized Firebase app
